import React, { Component } from "react";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import "./sannefa.css";
import { connect } from "react-redux";
import {getPlatJourSData} from "../../../actions/sannefaaction"


import {MDBRow, MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';




class CartePlatjourS extends React.Component {

  componentDidMount() {
    this.props.platJour();
  }
  render(){
return (

<div className="cardes">
<MDBRow>
{this.props.platdujourS.map(() => {
<MDBCol style={{ maxWidth: "22rem" }}>
      <MDBCard>
        <MDBCardImage className="img-fluid" src={el.image}
          waves />
        <MDBCardBody>
          <MDBCardTitle>{el.titre}</MDBCardTitle>
          <MDBCardText>{el.ingredient} </MDBCardText>
          <MDBCardText>{el.price} </MDBCardText>
          <MDBCardText>{el.region} </MDBCardText>
          <MDBCardText>{el.gouvernorat} </MDBCardText>


        </MDBCardBody>
      </MDBCard>
    </MDBCol>})}
    </MDBRow>
</div> 
)
}
}
const mapStateToProps = (state) => {
  return {
    platdujourS: state.platdujourSS,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    platJour: () => dispatch(getPlatJourSData()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CartePlatjourS);
