import React, { Component } from "react";
import {
  MDBIcon,
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
} from "mdbreact";
import { Fragment } from "react";
import { connect } from "react-redux";
import { addPlatJourSToApi } from "../../../actions/sannefaaction";

class AddPlatjour extends Component {
  state = {
    modal14: false,
  };

  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };

  render() {
    return (
      <MDBContainer>
        <MDBBtn
          floating="TRUE"
          tag="a"
          className="ml-auto mr-4 lighten-3 mdb-coalor"
          action
          onClick={this.toggle(14)}
        >
          <MDBIcon icon="plus" className="mdb-color lighten-3" />
        </MDBBtn>

        <MDBModal isOpen={this.state.modal14} toggle={this.toggle(14)} centered>
          <MDBModalHeader toggle={this.toggle(14)}></MDBModalHeader>

          <MDBModalBody>
            <Fragment>
              <div className="form-group">
                <label htmlFor="example1">image</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-lg"
                  onChange={(e) => this.setState({ image: e.target.value })}
                />
              </div>

              <div className="form-group">
                <label htmlFor="example1">titre</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-md"
                  onChange={(e) => this.setState({ title: e.target.value })}
                />
              </div>
              <div className="form-group">
                <label htmlFor="example1">ingredient</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-md"
                  onChange={(e) =>
                    this.setState({ ingredient: e.target.value })
                  }
                />
              </div>

              <div className="form-group">
                <label htmlFor="example1">prix</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-sm"
                  onChange={(e) => this.setState({ prix: e.target.value })}
                />
              </div>

              <div className="form-group">
                <label htmlFor="example1">Gouvernorat</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-md"
                  onChange={(e) =>
                    this.setState({ gouvernorat: e.target.value })
                  }
                />
              </div>

              <div className="form-group">
                <label htmlFor="example1">Région</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-md"
                  onChange={(e) => this.setState({ région: e.target.value })}
                />
              </div>
            </Fragment>
          </MDBModalBody>
          <MDBModalFooter>
            <center>
              {" "}
              <MDBBtn color="primary" onClick={this.toggle(14)}>
                Close
              </MDBBtn>
              <MDBBtn color="warning" color="warning" onClick={() =>
                this.props.platjouradded({
                  title: this.state.title,
                  image: this.state.image,
                  prix: this.state.prix,
                 ingredient: this.state.ingredient,
                })
              }>
               ajouter
              </MDBBtn>
            </center>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    platjouradded: (el) => dispatch(addPlatJourSToApi(el)),
  };
};

export default connect(null, mapDispatchToProps)(AddPlatjour);
