import React, { Component } from 'react';
import {MDBRow,MDBCard, MDBCardImage, MDBIcon, MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';

class ModalPage extends Component {
state = {
  modal14: false
}

toggle = nr => () => {
  let modalNumber = 'modal' + nr
  this.setState({
    [modalNumber]: !this.state[modalNumber]
  });
}

render() {
  return (
      <MDBContainer>
            <MDBBtn
                floating
                tag="a"
                className="ml-auto mr-4 lighten-3 mdb-coalor"
                action
                onClick={this.toggle(14)}
              >
                <MDBIcon icon="chevron-right" className="mdb-color lighten-3" />

              </MDBBtn>

        <MDBModal isOpen={this.state.modal14} toggle={this.toggle(14)} centered>
          <MDBModalHeader toggle={this.toggle(14)}>MDBModal title</MDBModalHeader>
          <MDBModalBody>
          <MDBCard cascade>
              <MDBCardImage
                cascade
                className="img-fluid"
                overlay="white-light"
                hover
                src="https://femmesdetunisie.com/wp-content/uploads/2015/12/Borghol.jpg"
              />
            <ModalPage/>
              <MDBCardBody cascade>
                <MDBCardTitle>Coucous bl 3alouch</MDBCardTitle>
                <hr />
                <MDBCardText>
                  <MDBCardText>Ingredient </MDBCardText>
                  3alouch, felfel a7mer, kosksi smid, hrissa,zit zitouna
                </MDBCardText>
                <MDBCardText>8 TND</MDBCardText>
              </MDBCardBody>

              <div className="rounded-bottom mdb-color lighten-3 text-center pt-3">
                <center>
                  {" "}
                  <div className="white-text"> Soliman abeul</div>
                </center>
              </div>
            </MDBCard>


          </MDBModalBody>
          <MDBModalFooter>
          <center>  <MDBBtn color="secondary" onClick={this.toggle(14)}>Close</MDBBtn>
            <MDBBtn color="primary">Save changes</MDBBtn></center>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

export default ModalPage;
