import React, { Component } from "react";
import {
  MDBIcon,
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInput
} from "mdbreact";
import { Fragment } from "react";
import { connect } from "react-redux";
import { changeeDataPlatjour } from "../../../actions/sannefaaction";
import axios from "axios";

class ChangerPlat extends Component {
  state = {
    modal14: false,
  };

  toggle = (nr) => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber],
    });
  };
  fileSelectedHandler = (e) => {
    this.setState({ selectedFile: e.target.files[0] });
  };

  uploadHandler = () => {
    const fd = new FormData();
    fd.append("image", this.state.selectedFile);
    axios
      .post("http://localhost:1305/platjour/ajouter", fd)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };
  render() 
  {
    return (
      <MDBContainer>
        <MDBBtn
          floating="TRUE"
          tag="a"
          className="ml-auto mr-4 lighten-3 mdb-coalor"
          action
          onClick={this.toggle(14)}
        >
          <MDBIcon icon="edit" cgradient="purple" />Modifier
        </MDBBtn>

        <MDBModal isOpen={this.state.modal14} toggle={this.toggle(14)} centered>
          <MDBModalHeader toggle={this.toggle(14)}></MDBModalHeader>

          <MDBModalBody>
            <Fragment>
              <div className="form-group">
                <label htmlFor="example1">image</label>

                <MDBInput
              type="file"
              onChange={(e) => this.fileSelectedHandler(e)} valueDefault={this.props.platS.image}
              outline
            />
              </div>

              <div className="form-group">
                <label htmlFor="example1">titre</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-md"
                  valueDefault={this.props.platS.title}
                  onChange={(e) => this.setState({ title: e.target.value })}
                />
              </div>
              <div className="form-group">
                <label htmlFor="example1">ingredient</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-md"
                  onChange={(e) =>
                    this.setState({ ingredient: e.target.value })

                  }

                />
              </div>

              <div className="form-group">
                <label htmlFor="example1">prix</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-sm"
                  onChange={(e) => this.setState({ prix: e.target.value })}
                />
              </div>

              <div className="form-group">
                <label htmlFor="example1">Gouvernorat</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-md"
                  onChange={(e) =>
                    this.setState({ gouvernorat: e.target.value })
                  }
                />
              </div>

              <div className="form-group">
                <label htmlFor="example1">Région</label>
                <input
                  type="text"
                  id="example1"
                  className="form-control form-control-md"
                  onChange={(e) => this.setState({ region: e.target.value })}
                />
              </div>
            </Fragment>
          </MDBModalBody>
          <MDBModalFooter>
            <center>
              {" "}
              <MDBBtn color="primary" onClick={this.toggle(14)}>
                Close
              </MDBBtn>
              <MDBBtn color="warning" color="warning" onClick={() =>
                this.props.platChangé(this.state)
              }>
               ajouter
              </MDBBtn>
            </center>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    platChangé: (el) => dispatch(changeeDataPlatjour(el)),
  };
};

export default connect(null, mapDispatchToProps)(ChangerPlat );
