import React, { Component } from "react";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import "./sannefa.css";
import { connect } from "react-redux";
import {
  getPlatJourSData,
  supprimerPlatduData,
} from "../../../actions/sannefaaction";

import {
  MDBRow,
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBCol,
} from "mdbreact";
import ChangerPlat from "./changerplat";

class CartePlatjourS extends React.Component {
  componentDidMount() {
    this.props.platJour();
  }
  render() {
    return (
      <div className="thecards">
        {this.props.platdujourS.map((el, i) => (
          <MDBCard style={{ maxWidth: "22rem" }} key={i}>
            <MDBCardImage className="img-fluid" src={el.image} waves />
            <MDBCardBody>
              <MDBCardTitle>{el.titre}</MDBCardTitle>
              <MDBCardText>{el.ingredient} </MDBCardText>
              <MDBCardText>{el.price} </MDBCardText>
              <div className="rounded-bottom mdb-color lighten-3 text-center pt-3">
                <MDBCardText>Localisation:{el.region} </MDBCardText>-
                <MDBCardText>{el.gouvernorat} </MDBCardText>
              </div>
              <MDBBtn onClick={() => this.props.supprimer(el._id)}>
                supprimer
              </MDBBtn>
              <div>
                {" "}
                <ChangerPlat  plas/>
              </div>
            </MDBCardBody>
          </MDBCard>
        ))}
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  platdujourS: state.platdujourSS,
});
const mapDispatchToProps = (dispatch) => ({
  platJour: (el) => dispatch(getPlatJourSData(el)),
  supprimer: (id) => dispatch(supprimerPlatduData(id)),
});

export default connect(mapStateToProps, mapDispatchToProps)(CartePlatjourS);
